#デッキの総変化量
def f1(x):
    sum = 0
    for i in range(len(x)):
        sum += abs(x[i] - INITIAL_DECK[i])
    print("デッキの総変化量" + str(sum))

#調整されたカード枚数
def f2(x):
    sum = 0
    ischanged = False
    for i in range(len(x)):
        if x[i] != INITIAL_DECK[i]:
            ischanged = True
        if i % 3 == 2 and i != 0:
            if ischanged:
                sum += 1
            ischanged = False
    print("調整されたカード枚数"+ str(sum))

for x in x_list:
    print(x)
    f1(x)
    f2(x)